import { OxdIcon } from '@ohrm/oxd';
export default {
  name: 'TrackerLogRatingButton',
  components: {
    'oxd-icon': OxdIcon
  },
  props: {
    label: {
      type: String,
      required: true
    },
    selected: {
      type: Boolean,
      required: true
    },
    type: {
      type: String,
      required: true,
      validator: function (value) {
        return ['positive', 'negative'].indexOf(value) !== -1;
      }
    }
  },
  emits: ['click'],
  computed: {
    iconName() {
      return `thumbs${this.type === 'positive' ? 'up' : 'down'}`;
    },
    buttonClasses() {
      return {
        'orangehrm-tracker-rating-button': true,
        '--deselected': !this.selected
      };
    },
    iconClasses() {
      return {
        'orangehrm-tracker-rating-icon': true,
        [`--${this.type}`]: true
      };
    }
  }
};
