import { ref, onBeforeMount } from 'vue';
import usei18n from '@/core/util/composable/usei18n';
import { APIService } from '@/core/util/services/api.service';
export default {
  name: 'VacancyDropdown',
  props: {
    status: {
      type: Boolean,
      required: false,
      default: null
    },
    excludeInterviewers: {
      type: Boolean,
      required: false,
      default: false
    }
  },
  setup(props) {
    const options = ref([]);
    const {
      $t
    } = usei18n();
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/recruitment/vacancies');
    onBeforeMount(() => {
      const params = {
        model: 'summary',
        limit: 0
      };
      if (props.status !== null) {
        params.status = props.status;
      }
      params.excludeInterviewers = props.excludeInterviewers;
      http.getAll(params).then(({
        data
      }) => {
        options.value = data.data.map(item => {
          return {
            id: item.id,
            label: item.status === false ? `${item.name} (${$t('general.closed')})` : item.name
          };
        });
      });
    });
    return {
      options
    };
  }
};
