import { APIService } from '@/core/util/services/api.service';
import RecruitmentStatus from '@/orangehrmRecruitmentPlugin/components/RecruitmentStatus';
import CandidateProfile from '@/orangehrmRecruitmentPlugin/components/CandidateProfile';
import HistoryTable from '@/orangehrmRecruitmentPlugin/components/HistoryTable';
export default {
  components: {
    'history-table': HistoryTable,
    'candidate-profile': CandidateProfile,
    'recruitment-status': RecruitmentStatus
  },
  props: {
    candidateId: {
      type: Number,
      required: true
    },
    maxFileSize: {
      type: Number,
      required: true
    },
    allowedFileTypes: {
      type: Array,
      required: true
    },
    updatable: {
      type: Boolean,
      required: false,
      default: true
    }
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/recruitment/candidates');
    return {
      http
    };
  },
  data() {
    return {
      candidate: null
    };
  },
  beforeMount() {
    this.onCandidateUpdate();
  },
  methods: {
    onCandidateUpdate() {
      this.http.get(this.candidateId).then(({
        data: {
          data
        }
      }) => {
        this.candidate = data;
      });
    }
  }
};
