import { ref, onBeforeMount } from 'vue';
import { APIService } from '@ohrm/core/util/services/api.service';
export default {
  name: 'SubunitDropdown',
  setup() {
    const options = ref([]);
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/subunits');
    onBeforeMount(() => {
      http.getAll().then(({
        data
      }) => {
        options.value = data.data.map(item => {
          return {
            id: item.id,
            label: item.name,
            _indent: item.level ? item.level + 1 : 1
          };
        });
      });
    });
    return {
      options
    };
  }
};
