export default {
  name: 'FullNameInput',
  inheritAttrs: false,
  props: {
    firstName: {
      type: String,
      required: true
    },
    middleName: {
      type: String,
      required: true
    },
    lastName: {
      type: String,
      required: true
    },
    rules: {
      type: Object,
      required: true
    },
    disabled: {
      type: Boolean,
      default: false
    },
    showMiddleNamePlaceholder: {
      type: Boolean,
      default: true
    },
    label: {
      type: String,
      default: null
    }
  },
  emits: ['update:firstName', 'update:middleName', 'update:lastName'],
  computed: {
    classes() {
      return {
        label: {
          'oxd-input-field-required': true
        },
        wrapper: {
          '--name-grouped-field': true
        }
      };
    },
    localizedLabel() {
      return this.label ? this.label : this.$t('general.employee_full_name');
    }
  }
};
